%Matlab读地震波形SAC文件fid=fopen('sacfile','r','ieee-be');%open SAC file%ieee-be is the format on Sun platform%if the format of sac is pc, there should be ieee-leA=fread(fid,[70,1],'float32');%Read SAC file headerB=fread(fid,[40,1],'int32');C=char(fread(fid,[1,192],'char'));D=fread(fid,'float32');%Read SAC file dataA(A==-12345.0)=NaN;B(B==-12345)=NaN;%set undefined valuesfclose(fid);%close SAC file%My sacname = 'A111.A602.2007.D.SAC.+';fid = fopen(sacname);rhdr = fread(fid,70,'real*4');ihdr = fread(fid,40,'integer*4');chdr = fread(fid,192,'char');data = fread(fid,'real*4');fclose(fid);